import React, {useState, useEffect} from 'react'
import { Link } from 'react-router-dom'

const ListHospital = () => {

    const [hospitals, setHospitals] = useState([])

    useEffect(() => {

        getAllHospitals();
    }, [])

    const getAllHospitals = () => {
        HospitalService.getAllHospitals().then((response) => {
            setHospitals(response.data)
            console.log(response.data);
        }).catch(error =>{
            console.log(error);
        })
    }

    const deleteHospital = (hospitalId) => {
       HospitalService.deleteHospital(hospitalId).then((response) =>{
        getAllHospitals();

       }).catch(error =>{
           console.log(error);
       })
        
    }

    return (
        <div className = "container">
            <h2 className = "text-center"> List Hospitals </h2>
            
            <table className="table table-bordered table-striped">
                <thead>
                    <th> Hospital Id </th>
                    <th> Hospital Name </th>
                    <th> Hospital Number </th>
                    <th> Hospital Location </th>
                    <th> Hospital Pincode </th>
                    <th> Actions </th>
                </thead>
                <tbody>
                    {
                        hospitals.map(
                            hospital =>
                            <tr key = {hospital.id}> 
                                <td> {hospital.id} </td>
                                <td> {hospital.name} </td>
                                <td>{hospital.number}</td>
                                <td>{hospital.location}</td>
                                <td>{hospital.pincode}</td>
                                <td>
                                   
                                    <button className = "btn btn-danger" onClick = {() => deleteHospital(hospital.id)}
                                    style = {{marginLeft:"10px"}}> Delete</button>
                                </td>
                            </tr>
                        )
                    }
                </tbody>
            </table>
        </div>
    )
}

export default ListHospital
